    exec("npx prisma migrate dev --name v4 && npx prisma db push")
    console.log(`server is running on port ${process.env.PORT || 3030}`)
  })
  process.on('SIGTERM', () => {
    server.close(() => {
      PrismaHelper.$disconnect()
      process.exit(0)
