app.use((err, req, res, next) => {
  // Print error
  console.error(err.stack);

  function write() {
    res.status(500).send('Internal server error - see logs for details');
  }

  if (req.complete) {
    return write();
  }

  // Terminate request if there is unsent data
  req.unpipe();
  req.on('end', write);
  req.resume();
});
