app.use('/failure', failureRouter);

app.use(function (err, req, res, next) {
  res.status(500).send('error');
});

var tests = [
  function (next) {
    request(app)
      .get('/success')
      .expect(200, SUCCESS_MESSAGE, next);
  },
  function (next) {
    request(app)
      .get('/failure')
      .expect(500, FAILURE_MESSAGE, next);
  }
