 */

methods.concat('all').forEach(function(method){
  app[method] = function(path){
    if ('get' == method && 1 == arguments.length) return this.set(path);

    var route = this.route(path);
    route[method].apply(route, slice.call(arguments, 1));
    return this;
  };
});
