I made a little code which can help us developing better and more organized our route filters. I liked it, and it will be nice if someone implement it on Express 5 or 4. Hope you guys enjoy :+1: 

``` js
var express = require('express');
var path = require('path');

var app = express();

var filters = [];

function Filter (name, fn) {
    return filters.push({
        name: name,
        filter: fn
    });
}

function Group (app, opts, fn) {
    var self = this;
    var prefix = this.prefix = opts && opts.prefix || '';
    this.filter = opts && opts.filter || false;

    function defineFilter (filter) {
        filters.forEach(function (key) {
            if(key.name === filter) {
                filter = key.filter;
            }
        });
        return filter;
    }

    if(this.filter) {
        /**
         * Transforms this: 'SomeFilter'
         * On this: ['SomeFilter']
         */
        if(typeof this.filter === 'string') {
            this.filter = [this.filter];
        }

        if(this.filter instanceof Array) {
            this.filter.forEach(function (filter, i) {
                self.filter[i] = defineFilter(filter);
            });
        }
    }

    this.route = function (path) {
        var route = app.route(prefix + path);

        if(self.filter) {
            self.filter.forEach(function (key) {
                if(typeof key !== 'function') {
                    return;
                }

                route.all(key);
            });
        }

        return route;
    };

    this.group = function (opts, fn) {
        if(opts && opts.prefix) {
            opts.prefix = self.prefix + opts.prefix;
        }

        return new Group (app, opts, fn);
    };

    fn.call(this, this);
}

new Filter('SomeFilter', function (req, res, next) {
    res.json({ msg: 'Stay here, please.' });
});

new Group(app, {
    filter: 'SomeFilter', // filter
    // or:
    // filter: ['SomeFilter', 'AnotherFilter']
    prefix: '/api'
}, function (groupOne) {
        // It is important to remember that you should use 'this' or 'app' inside of the callback scope, or it will not work properly.
    // GET /api/user
    this
        .route('/user')
        .get(function (req, res, next) {
            res.json([1, 2, 3, 4]);
        });

    this.group({
        prefix: '/user'
    }, function (groupTwo) {
        // GET /api/user/list
        groupTwo // or this
            .route('/list')
            .get(function (req, res, next) {
                res.json({ isJavascript: true });
            });

        // GET /api/user/prefix/:prefix
        this
            .group({
                prefix: '/prefix'
            }, function (groupThree) {
                groupThree.route('/:prefix').get(function (req, res) {
                    res.json({ itWorks: true });
