const isFunction = value => typeof value === 'function';
const identity = arg => arg;
const howMany = (collection, iteratee) => {
  const obj = Object(collection);
  const pairsArray = isFunction(obj.entries) ?
    Array.from(obj.entries()) :
    Object.entries(obj);

  const fn = isFunction(iteratee) ? iteratee : identity;
  const counts = pairsArray.reduce((acc, pair) => {
    const value = fn(pair);
    return acc.set(value, acc.has(value) ? acc.get(value) + 1 : 1);
