```
const toInteger = (value) => {
  const num = Number(value) || 0;
  return num === 0 || isFinite(num) === false ? num : Math.trunc(num);
};

const isStringPrimitive = value => typeof value === 'string';
const isIntegerString = value => toInteger(value).toString() === value;
const clamp = (value, min, max) => Math.min(Math.max(value, min), max);

const handler = {
  get(target, name) {
    const value = target[name];
    if (Number.isSafeInteger(value)) {
      return value;
    }

    if (isIntegerString(name)) {
      const integer = Number(name);
      return Object.keys(target).find(key => target[key] === integer);
    }

    return undefined;
  },
  set() {
    throw new SyntaxError('Can not set a value on an enum');
  }
};

const Enum = (...names) => {
  const auto = new Set();
  const manual = new Map();
  let count = 0;
  names.forEach((item, index) => {
    if (isStringPrimitive(item)) {
      if (isIntegerString(item)) {
        throw new SyntaxError(`item is an invalid pattern: ${item}`);
      }

      if (auto.has(item) || manual.has(item)) {
        throw new TypeError(`item is not unique: ${item}`);
      }

      auto.add(item);
    } else if (Array.isArray(item) && item.length === 2) {
      const key = item[0];
      if (isStringPrimitive(key) === false) {
