      }

      if (isIntegerString(key)) {
        throw new SyntaxError(`key is an invalid pattern: ${key}`);
      }

      if (auto.has(key) || manual.has(key)) {
        throw new Error(`key is not unique: ${key}`);
      }

      const value = item[1];
      if (value < 0 || Number.isSafeInteger(value) === false) {
        const msg = `value of array pair is not a safe integer: ${value}`;
        throw new RangeError(msg);
      }

      manual.forEach((v) => {
        if (value === v) {
          throw new SyntaxError('value of array pair is not unique');
        }
      });

      if (index === 0) {
        count = value;
      }

      manual.set(key, value);
    } else {
      throw new TypeError('item is not a string or an array pair');
    }
  });

  const pairs = [];
  const values = new Set();
  manual.forEach((value, key) => {
    pairs.push({ key, value });
    values.add(value);
  });

  auto.forEach((key) => {
    while (values.has(count)) {
      count += 1;
    }

    if (Number.isSafeInteger(count) === false) {
      throw new RangeError(`count is not a safe integer: ${count}`);
    }

    pairs.push({ key, value: count });
    count += 1;
  });

  const empty = pairs
    .sort((a, b) => clamp(a.value - b.value, -1, 1))
    .reduce((obj, pair) => Object.defineProperty(obj, pair.key, {
      value: pair.value,
